sys_script_include.script:
var ERPSourceConfigCRUD = Class.create();

ERPSourceConfigCRUD.TABLE = 'sn_fcms_intg_source';

ERPSourceConfigCRUD.getActiveSourceConfigs = function(){
	var encodedQuery = 'inactive=false' 
						+ '^fin_erp' + 'ISNOTEMPTY'
						+ '^fin_erp.active' + '=true';
	return new ERPSourceConfigCRUD().getByQuery(encodedQuery);
};

ERPSourceConfigCRUD.prototype = Object.extendsObject(sn_shop.SpendBaseCRUD,{
   initialize: function() {
	   this.table = ERPSourceConfigCRUD.TABLE;
    },

	type: 'ERPSourceConfigCRUD'
});
