sys_script_include.script:
var IntegrationServiceMap = Class.create();

IntegrationServiceMap.TABLE = 'sn_fcms_intg_service_map';

IntegrationServiceMap.getServiceMapsForSource = function(serviceTypes, sourceId){
	var encodedQuery = 'serviceIN' + serviceTypes
						+ '^source=' + sourceId;
	return new IntegrationServiceMap().getByQuery(encodedQuery);
};

IntegrationServiceMap.prototype = Object.extendsObject(sn_shop.SpendBaseCRUD,{
   initialize: function() {
	   this.table = IntegrationServiceMap.TABLE;
    },

	type: 'IntegrationServiceMap'
});
