sys_script_include.script:
var SpendPostERPResponseImpl = Class.create();
SpendPostERPResponseImpl.prototype = {
    initialize: function() {
    },

	canProcess: function () {
		return true;
	},

    process: function (/*JSObject*/ result) {
		var serviceName = result.service.service;
		if (serviceName == SpendIntgConstants.ERP_SERVICE_POST_PO)
			PostPurchaseOrderService.handleResponse(result);
		else if (serviceName == SpendIntgConstants.ERP_SERVICE_POST_GR)
			PostGoodsReceiptService.handleResponse(result);
		else if (serviceName == SpendIntgConstants.ERP_SERVICE_POST_INVOICE)
			PostInvoiceService.handleResponse(result);
    },

    type: 'SpendPostERPResponseImpl'
};
