sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

    var requisitionId = sn_shop.PurchaseOrder.getBackingRequisitionId(source.po_id);
    var purchaseRequisitionUtil = new sn_shop.PurchaseRequisitionUtil(requisitionId);
    var taskGr = sn_shop.SpendTask.getOpenPOIntegrationErrorTask(requisitionId);
    if (gs.nil(taskGr)) {
        purchaseRequisitionUtil.updatePostedToERPFlagOfPurchaseRequisition('success');
        if (purchaseRequisitionUtil.isPostedToThirdPartyFlagOfPurchaseRequisitionSuccessful())
            sn_shop.PurchaseRequisitionUtil.confirmOrderCreation(requisitionId);
    }


})(source, map, log, target);
