sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {
	var receiptGr = new GlideRecord('sn_shop_receipt');
	if(!gs.nil(source.goods_receipt_id) && receiptGr.get(source.goods_receipt_id)) {
		if(sn_shop.SpendTaskUtil.hasOpenGRIntegrationErrorTask(receiptGr))
			sn_shop.ReceiptUtil.updateReceiptPendingReview(receiptGr);
		else
			sn_shop.ReceiptUtil.updateReceiptPosted(receiptGr);
	}
})(source, map, log, target);
