sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {
    if (action == 'insert') {
        ignore = true;
		return;
    }

    if (gs.nil(source.asset_id) || gs.nil(source.erp_asset_num) || gs.nil(source.asset_num)) {
        ignore = true;
        return;
    }

    var erpSource = new sn_fin.ErpSource(source.getValue('erp_source'));
    if (!erpSource.isValid()) {
        var msg = gs.getMessage('Invalid ERP Source');
        log.error(msg);
        status_message = msg;
        ignore = true;
        return;
    }

})(source, map, log, target);
