sysauto_script.script:
var erpConfigGr = ERPSourceConfigCRUD.getActiveSourceConfigs();
var sourceConfig;
while (erpConfigGr.next()) {
    sourceConfig = new ErpSourceConfig(erpConfigGr.getValue('fin_erp'));
    if (!sourceConfig.isValid()) {
        gs.error(gs.getMessage("ERP Source Configuration is not valid for {0}", [erpConfigGr.getValue('source')]));
        continue;
    }
    var response = SpendDataService.doFetch( sourceConfig, SpendIntgConstants.ERP_SERVICE_FETCH_FIXED_ASSETS_DETAILS);
    if (response.status != IntgConstants.STATUS_SUCCESS)
        gs.error("Error in fetching fixed asset details data for " + sourceConfig.getValue('name') + ': ' + response.message);
    else
        gs.debug("Fetching fixed asset details data completed successfully for " + sourceConfig.getValue('name'));
}
