sys_transform_entry.source_script:
answer = (function transformEntry(source) {	
	var targetErpDocumentNumber = gs.nil(target.getValue('erp_document_id')) ? "" : target.getValue('erp_document_id');
	var sourceErpDocumentNumber = gs.nil(source.getValue('erp_document_number')) ? "" : source.getValue('erp_document_number');

	if(gs.nil(targetErpDocumentNumber))
		return sourceErpDocumentNumber;
	else if(targetErpDocumentNumber.search(sourceErpDocumentNumber) >= 0)
		return targetErpDocumentNumber;
	return targetErpDocumentNumber + ', ' + sourceErpDocumentNumber;
})(source);
