sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {
	var targetErrors = gs.nil(target.getValue('errors')) ? "" : target.getValue('errors');
	var sourceErrors = gs.nil(source.getValue('errors')) ? "" : source.getValue('errors');

	if(source.target_table != 'x_trint_fcms_journal_line' || gs.nil(sourceErrors) || targetErrors.search(sourceErrors) >= 0)
		ignore = true;

    if (gs.nil(target.getValue('sys_id'))) {
		log.error(gs.getMessage('Target sys_id is not available in Journal Line table - {0}', source.getValue('target_sys_id')));
		ignore = true;
	}

})(source, map, log, target);
