sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

	if ( source.result != 'success' || gs.nil(source.reversal_document_id) )
		return;

	var targetReversalDocNumber = target.getValue('reversal_document_id').split(',');
	var sourceReversalDocNumber = source.reversal_document_id;

	if(!gs.nil(sourceReversalDocNumber) && targetReversalDocNumber.indexOf( sourceReversalDocNumber ) == -1)
		targetReversalDocNumber.push(sourceReversalDocNumber);

	source.reversal_document_id = targetReversalDocNumber.join(',');

})(source, map, log, target);
