sn_fcms_intg_service_element_map.script:
answer = (function getValueForTarget(current, erpIntgCtxString) {
	var jobOption1 = current.getValue('date_option_1');
	var value = current.getValue('value_1');

	if (!gs.nil(jobOption1) && (jobOption1 != value))
		value = x_trint_fin_rpa.JobParamEvaluator.compute(current.getValue('sys_id'), jobOption1);

	var dataType = current.input_parameter.data_type.toString();

	switch (dataType) {
		case 'date':
			if (!gs.nil(value))
				value = value.replace(/-/g, '');
			break;
		case 'boolean':
			if (!gs.nil(value) && value != false && value != 'false')
				value = 'X';
			else
				value = '';
			break;
	}
	return value;
})(current, erpIntgCtxString);
