sn_fcms_intg_service_element_map.script:
answer = (function getValueForTarget(current, erpIntgCtxString) {
	var jobOption2 = current.getValue('date_option_2');
	var value = current.getValue('value_2');

	if (!gs.nil(jobOption2) && (jobOption2 != value))
		value = x_trint_fin_rpa.JobParamEvaluator.compute(current.getValue('sys_id'), jobOption2);

	var dataType = current.input_parameter.data_type.toString();

	switch (dataType) {
		case 'date':
			value = value.replace(/-/g, '');
			break;
		case 'boolean':
			if (!gs.nil(value) && value != false && value != 'false')
				value = 'X';
			else
				value = '';
			break;
	}
	return value;
})(current, erpIntgCtxString);
